firestore.rules:
rules_version = '2';

service cloud.firestore {
  match /databases/{database}/documents {

    // --- Helper Functions ---
    function isAdmin() {
      return request.auth != null && request.auth.token.email == "sagralnarey@gmail.com";
    }

    function isSignedIn() {
      return request.auth != null;
    }

    // --- User-Specific Data ---

    // Public user profiles, but only owners can write.
    match /users/{userId} {
      allow read: if true;
      allow write: if request.auth.uid == userId;
    }

    // Users can only manage their own favorites and notifications.
    match /users/{userId}/{collection}/{docId} {
        allow read, write: if request.auth.uid == userId;
    }
    match /favorites/{userId} {
      allow read, write: if request.auth.uid == userId;
    }

    // --- Public Read, Admin Write (Most of the app's data) ---
    match /news/{articleId} { allow get: if true; allow list, write: if isAdmin(); }
    match /leagueCustomizations/{leagueId} { allow get, list: if true; allow write: if isAdmin(); }
    match /countryCustomizations/{countryName} { allow get, list: if true; allow write: if isAdmin(); }
    match /continentCustomizations/{continent} { allow get, list: if true; allow write: if isAdmin(); }
    match /teamCustomizations/{teamId} { allow get, list: if true; allow write: if isAdmin(); }
    match /playerCustomizations/{playerId} { allow get, list: if true; allow write: if isAdmin(); }
    match /coachCustomizations/{coachId} { allow read: if true; allow write: if isAdmin(); }
    match /statisticCustomizations/{statName} { allow read: if true; allow write: if isAdmin(); }
    match /adminFavorites/{teamId} { allow read: if true; allow write: if isAdmin(); }
    match /managedCompetitions/{leagueId} { allow read: if true; allow write: if isAdmin(); }
    match /iraqiLeagueTopScorers/{scorerId} { allow read: if true; allow write: if isAdmin(); }
    match /dailyGlobalPredictions/{date} { allow read: if true; allow write: if isAdmin(); }

    // --- Prediction System ---

    // Leaderboard is public, but only owners or admins can update their own score.
    match /leaderboard/{userId} {
      allow read: if true;
      allow write: if request.auth.uid == userId || isAdmin();
    }

    // Predictions are public, but only owners can manage their own predictions.
    match /predictions/{predictionId} {
      allow read: if true;
      allow write: if isSignedIn() && request.resource.data.userId == request.auth.uid;
    }
    match /seasonPredictions/{predictionId} {
      allow read: if true;
      allow write: if isSignedIn() && request.resource.data.userId == request.auth.uid;
    }

    // --- Comments and Interactions ---
    match /matches/{matchId} {
      // Match metadata (like commentsEnabled) is public, admin-write
      allow read: if true;
      allow write: if isAdmin();

      // Comments and their subcollections (replies, likes)
      match /comments/{commentId} {
        allow read: if true;
        allow create: if isSignedIn();
        allow update, delete: if isSignedIn() && (resource.data.userId == request.auth.uid || isAdmin());

        match /replies/{replyId} {
          allow read: if true;
          allow create: if isSignedIn();
          allow update, delete: if isSignedIn() && (resource.data.userId == request.auth.uid || isAdmin());

           match /likes/{userId} {
              allow read: if true;
              allow create, delete: if request.auth.uid == userId;
           }
        }

        match /likes/{userId} {
          allow read: if true;
          allow create, delete: if request.auth.uid == userId;
        }
      }
    }
  }
}
