firestore.rules:
rules_version = '2';
service cloud.firestore {
  match /databases/{database}/documents {

    // ---------------------------------------------------
    // 🔹 Helper Function: المدير فقط
    // ---------------------------------------------------
    function isAdmin() {
      return request.auth != null && request.auth.token.email == "sagralnarey@gmail.com";
    }

    // ---------------------------------------------------
    // 👑 صلاحيات المدير الكاملة
    // ---------------------------------------------------
    match /{document=**} {
      allow read, write: if isAdmin();
    }

    // ---------------------------------------------------
    // 👤 المستخدمين (Users)
    // ---------------------------------------------------
    match /users/{userId} {
      allow read: if request.auth != null;
      allow create, update, delete: if request.auth != null && request.auth.uid == userId;
    }

    match /users/{userId}/notifications/{notificationId} {
      allow read, update, delete: if request.auth != null && request.auth.uid == userId;
      allow create: if request.auth != null && request.resource.data.senderId == request.auth.uid;
    }

    // ---------------------------------------------------
    // ⭐ المفضلات (Favorites)
    // ---------------------------------------------------
    match /favorites/{userId} {
      allow read, write, update: if request.auth != null && request.auth.uid == userId;
    }

    match /adminFavorites/{teamId} {
      allow read: if request.auth != null;
    }

    // ---------------------------------------------------
    // 🎨 التخصيصات (Customizations)
    // ---------------------------------------------------
    match /leagueCustomizations/{leagueId}      { allow read: if request.auth != null; }
    match /countryCustomizations/{countryName}  { allow read: if request.auth != null; }
    match /continentCustomizations/{continent} { allow read: if request.auth != null; }
    match /teamCustomizations/{teamId}          { allow read: if request.auth != null; }
    match /playerCustomizations/{playerId}      { allow read: if request.auth != null; }
    match /coachCustomizations/{coachId}        { allow read: if request.auth != null; }

    // ---------------------------------------------------
    // ⚽ المباريات والتعليقات (Matches & Comments)
    // ---------------------------------------------------
    match /matches/{matchId} {
      allow read, list: if request.auth != null;

      match /comments/{commentId} {
        allow read: if request.auth != null;
        allow create: if request.auth != null;
        allow update, delete: if request.auth != null && resource.data.userId == request.auth.uid;

        match /likes/{userId} {
          allow read: if request.auth != null;
          allow create, delete: if request.auth.uid == userId;
        }

        match /replies/{replyId} {
          allow read: if request.auth != null;
          allow create: if request.auth != null;
          allow update, delete: if request.auth != null && resource.data.userId == request.auth.uid;

          match /likes/{userId} {
            allow read: if request.auth != null;
            allow create, delete: if request.auth.uid == userId;
          }
        }
      }
    }

    // ---------------------------------------------------
    // 🏆 الترتيب (Leaderboard)
    // ---------------------------------------------------
    match /leaderboard/{userId} {
      allow read, list: if request.auth != null;
      allow update: if request.auth != null && (request.auth.uid == userId || isAdmin());
    }

    // ---------------------------------------------------
    // 🌍 التوقعات اليومية (Daily Global Predictions)
    // ---------------------------------------------------
    match /dailyGlobalPredictions/{date} {
      allow read, list: if request.auth != null;
    }

    // ---------------------------------------------------
    // 🎯 التوقعات الفردية للمباريات (Match Predictions)
    // ---------------------------------------------------
    match /predictions/{predictionId} {
      allow create, update, delete: if request.auth != null && request.resource.data.userId == request.auth.uid;
      allow get, list: if request.auth != null;
    }

    // ---------------------------------------------------
    // 🧩 بطاقات الموسم (Season Cards)
    // ---------------------------------------------------
    match /seasonCards/{userId} {
      allow read, list: if request.auth != null;
      allow create, update, delete: if request.auth != null && (request.auth.uid == userId || isAdmin());
    }

    // ---------------------------------------------------
    // 🏅 توقعات الموسم (Season Predictions)
    // ---------------------------------------------------
    match /seasonPredictions/{docId} {
      allow read, list: if request.auth != null;

      // المستخدم يمكنه فقط إنشاء أو تعديل توقعه الخاص
      allow create, update, delete: if request.auth != null
        && request.resource.data.userId == request.auth.uid
        && request.resource.data.leagueId is int
        && request.resource.data.season is int
        && (request.resource.data.predictedChampionId is int || request.resource.data.predictedTopScorerId is int);
    }

    // ---------------------------------------------------
    // 📰 الأخبار (News)
    // ---------------------------------------------------
    match /news/{newsId} {
      // ✅ أي شخص (حتى غير المسجل) يمكنه القراءة
      allow read, list: if true;

      // 🧑‍💻 النشر والتعديل والحذف فقط للمدير
      allow create, update, delete: if isAdmin();
    }
  }
}
